import { useDispatch, useSelector } from "react-redux";
import { handleFetch } from "../../utils";
import { displayTodo } from "../../store/actions/index";
import { useEffect } from 'react';

function TodoList () {
    const dispatch = useDispatch();
    const todo = useSelector(state => state.todo.todo);
    const url = 'https://jsonplaceholder.typicode.com/todos';

    // useEffect(() => {
    //     const displayTodoList = () => {
    //         dispatch(handleFetch(url, displayTodo));
    //     }

    //     displayTodoList();
    // }, []);

    let fetchData = [];

    async function handleFetch (url) {
        try {
            const response = await fetch(url);

            fetchData = await response.json();
        } catch (error) {
            return [];
        }

        return fetchData;
    };

        useEffect(() => {
            const displayUserList = async () => {
                await handleFetch(url)
                dispatch(displayTodo(fetchData));
            }

            displayUserList();
        }, []);



    return (
        <div className="content">
            {todo.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Completed</th>
                        </tr>
                    </thead>
                   {todo.map((todo) => {
                        return (
                            <tbody key={todo.id}>
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.title}</td>
                                    {todo.completed ?
                                        <td className="completed"></td>
                                    :
                                        <td className="not-completed"></td>
                                    }
                                </tr>
                            </tbody>
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default TodoList;
