import { useDispatch, useSelector } from "react-redux";
import { handleFetch } from "../../asyncActions/index";
import { displayPosts } from "../../store/actions/index";
import { displayTodo } from "../../store/actions/index";

function PostsList () {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const url = 'https://jsonplaceholder.typicode.com/posts';

    function displayPostList () {
        dispatch(handleFetch(url, displayPosts));
    }

    return (
        <div className="content">
            {displayPostList()}
            {posts.length > 0 ?
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Body</th>
                        </tr>
                    </thead>
                   {posts.map(post =>
                   <tbody>
                        <tr>
                            <td>{post.id}</td>
                            <td>{post.title}</td>
                            <td>{post.body}</td>
                        </tr>
                   </tbody>
                   )} 
                </table>
            :
                <div className='error'>
                    <h3>No posts</h3>
                </div>
            }
        </div>
    )
}

export default PostsList;
