import { DISPLAY_USERS, DISPLAY_POSTS, DISPLAY_TODO } from "../types";

const defaultState = {
    users: [],
    posts: [],
    todo: []
}

export const reducer = (state = defaultState, action) => {
    switch (action.type) {
        case DISPLAY_USERS: {
          return {
              ...state,
              users: [
                  ...state.users,
                  ...action.payload
              ]
          }
        }
        case DISPLAY_POSTS: {
            return {
                ...state,
                posts: [
                    ...state.posts,
                    ...action.payload
                ]
            }
        }
        case DISPLAY_TODO: {
            return {
                ...state,
                todo: [
                    ...state.todo,
                    ...action.payload
                ]
            }
        }

        default: return state;
      }

}
