import { useState, useEffect } from "react";

// export function handleFetch (url, action) {
//     return function (dispatch) {
//         fetch (url)
//         .then(response => response.json())
//         .then(result => dispatch(action(result)))
//         .catch(error => [])
//     }
// };

export function HandleFetch (url, action) {
    const [data, setData] = useState([]);

    useEffect(() => {
        return function (dispatch) {
            fetch (url)
            .then(response => response.json())
            .then(result => dispatch(action(result)))
            setData(result.data)
            .catch(error => [])
        }
    }, [])
};

