import { useEffect } from "react";
import { displayUsers } from "../store/actions/index";
import { useRef } from "react";

export function handleFetchUsers () {
    return function (dispatch) {
        useEffect(() => {
            fetch ('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => dispatch(displayUsers(json)))
        }, [])
    }
}

let fetchData = [];
const url = 'https://jsonplaceholder.typicode.com/users';

export async function handleFetchRequest(url) {
    try {
        const response = await fetch(url);

        fetchData = await response.json();
        dispatch(displayUsers(fetchData))
    } catch (error) {
        return [];
    }
}
