                    </ul>
                    )} 
                </div>
            :
                <div className='loading'></div>
            }
        </div>
    )

    return (
        <div className="content">
            {posts.length > 0 ?
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Body</th>
                        </tr>
                    </thead>
                   {posts.map(post =>
                   <tbody key={post.id}>
                        <tr>
                            <td>{post.id}</td>
                            <td>{post.title}</td>
                            <td>{post.body}</td>
                        </tr>
                   </tbody>
                   )} 
                </table>
            :
                <div className='error'>
                    <h3>No posts</h3>
                </div>
            }
        </div>
    )
}

export default PostsList;
