import { useState, useEffect } from "react";

export function HandleFetch (url, action) {
    const [data, setData] = useState([]);

    useEffect(() => {
        return function (dispatch) {
            fetch (url)
            .then(response => response.json())
            .then(result => dispatch(action(result)))
            setData(result)
            .catch(error => [])
        }
    })
};

