export function HandleFetch (url, action) {
    const [data, setData] = useState({data: []});

    return async function (dispatch) {
        try {
            const response = await fetch(url);
            const result = await response.json();

            result = dispatch(action(result));

            setData(result);
        } catch (error) {
            return [];
        }
    }
}
