    //                     </li>
    //                 })}
    //             </ul>
    //         :
    //             <div className='loading'></div>
    //         }
    //     </div>
    // )

    return (
        <div className='content'>
            {users.length > 0 ?
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Username</th>
                        </tr>
                    </thead>
                    {users.map(user =>
                        <tbody key={user.id}>
                            <tr>
                                <td>{user.name}</td>
                                <td>{user.username}</td>   
                            </tr>    
                        </tbody>   
                    )}
                </table>
            :
                <div className='loading'></div>
            }
        </div>
    )
}

export default UserList;
