import { useDispatch, useSelector } from 'react-redux';
import { handleFetch } from '../../utils';
import { displayUsers } from "../../store/actions/index";
import { useEffect } from 'react';

function UserList () {
    const dispatch = useDispatch();
    const users = useSelector(state => state.users.users);
    const userId = useSelector(state => state.users.users.map(user => user.id));
    const url = 'https://jsonplaceholder.typicode.com/users';

    // useEffect(() => {
    //     const displayUserList = () => {
    //         dispatch(handleFetch(url, displayUsers));
    //     }

    //     displayUserList();
    // }, []);

    let fetchData = [];

    async function handleFetch(url) {
        try {
            const response = await fetch(url);

            fetchData = await response.json();
        } catch (error) {
            return [];
        }
    }

    useEffect(() => {
        const displayUserList = async () => {
            await handleFetch(url);
            dispatch(displayUsers(fetchData));
        }

        displayUserList();
    }, []);

    return (
        <div className='content'>
            {users.length ? (
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Username</th>
                        </tr>
                    </thead>
                    {users.map(user => {
                        return (
                            <tbody key={user.id}>
                                <tr>
                                    <td>{user.name}</td>
                                    <td>{user.username}</td>   
                                </tr>    
                            </tbody>   
                        )
                    })}
                </table>
            ) : (
                <div className='loading'></div>
            )}
        </div>
    )
}

export default UserList;
