export function handleFetchUsers () {
    return function (dispatch) {
        fetch('https://jsonplaceholder.typicode.com/users')
        .then((response) => {
          return response.json();
        })
        .then((data) => {
            data = dispatch(displayUsers(data))
        });
    }
}
