export function handleFetch (url, action) {
    const [data, setData] = useState({data: []});

    return async function (dispatch) {
        try {
            const response = await fetch(url);
            const result = await response.json();

            return result = dispatch(action(result));
        } catch (error) {
            return [];
        }
    }
}
