import { useState, useEffect } from "react";

export function handleFetch (url, action) {
    const [data,  setData] = useState({data: []});

    return function (dispatch) {
        useEffect(() => {
            fetch (url)
            .then(response => response.json())
            .then(json => dispatch(action(json)))
        }, [])
    }
};
