import { COUNTER_INCREMENT, COUNTER_DECREMENT, COUNTER_RESET, DISPLAY_USERS } from "../types";

const defaultState = {
    count: 0
}

export const reducer = (state = defaultState, action) => {
    switch (action.type) {
      case COUNTER_INCREMENT: {
        return {
          ...state, 
          count: state.count + action.payload
        }
      }
      case COUNTER_DECREMENT: {
        return {
          ...state, 
          count: state.count - action.payload
        }
      }
      case COUNTER_RESET: {
        return {
            ...state,
            count: state.count = action.payload
        }
      }

      case DISPLAY_USERS: {
        return {
            ...state,
            users: [
                ...state.users,
                action.payload
            ]
        }
      }

      default: return state;
    }

}
