import { useDispatch, useSelector } from "react-redux";

function TodoList () {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts);

    function displayPostList () {
        dispatch(handleFetchPosts());

        console.log(posts);
    }

    return (
        <div className="content">
            {displayPostList()}
            {posts.length > 0 ?
                <table>
                    <th>ID</th>
                    <th>UserID</th>
                    <th>Title</th>
                    <th>Body</th>
                   {posts.map(post =>
                    <tr>
                        <td>{post.id}</td>
                        <td>{post.userId}</td>
                        <td>{post.title}</td>
                        <td>{post.body}</td>
                    </tr>
                   )} 
                </table>
            :
                <div className='error'>
                    <h3>No posts</h3>
                </div>
            }
        </div>
    )
}

export default TodoList;
