import { useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import './Navigation.css';
import { handleFetch } from '../../utils';
import { displayUsers } from "../../store/actions/index";

function Navigation () {
    const dispatch = useDispatch();
    const url = 'https://jsonplaceholder.typicode.com/users';

    return (
        <nav>
            <ul>
                <li><NavLink activeclassname="active" to="/posts">Posts</NavLink></li>
                <li><NavLink activeclassname="active" to="/todo">Todo</NavLink></li>
                <li><NavLink activeclassname="active" to="/users" onClick={() => dispatch(handleFetch(url, displayUsers))}>Users</NavLink></li>
            </ul>
        </nav>
    )
}

export default Navigation;
