import { displayUsers } from "../store/actions";

let fetchUsers = [];

export function handleFetchUsers() {
    const url = 'https://jsonplaceholder.typicode.com/users';

    return async function (dispatch) {
        try {
            const response = await fetch(url);
            // fetchUsers = await response.json();
            fetchUsers = dispatch(displayUsers(response));
        } catch (error) {
            return [];
        }
    }
}
