export function HandleFetchUsers () {
    useEffect(async () => {
        const [data, setData] = useState({ hits: [] });

        const result = await axios(
            'https://jsonplaceholder.typicode.com/users',
        );

        setData(dispatch(displayUsers(result.data)));
      });
}
